import React, { useState } from "react";
import "./Prescriptions.css";

function Prescriptions({ userRole }) {
  const [search, setSearch] = useState("");
  const [prescriptions, setPrescriptions] = useState([]);
  const [form, setForm] = useState({ patient: "", medication: "", dosage: "" });

  // Dummy data for demonstration
  const allPrescriptions = [
    { id: 1, patient: "John Doe", medication: "Ibuprofen", dosage: "200mg" },
    { id: 2, patient: "Jane Smith", medication: "Paracetamol", dosage: "500mg" },
  ];

  // Filter prescriptions based on search
  const filtered = allPrescriptions.filter(
    (p) =>
      p.patient.toLowerCase().includes(search.toLowerCase()) ||
      p.medication.toLowerCase().includes(search.toLowerCase())
  );

  // Handle form input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle prescription submission (for doctors)
  const handleSubmit = (e) => {
    e.preventDefault();
    // Add prescription logic here (e.g., API call)
    setPrescriptions([...prescriptions, { ...form, id: Date.now() }]);
    setForm({ patient: "", medication: "", dosage: "" });
  };

  return (
    <div className="prescriptions-container">
      <h2>Prescriptions</h2>
      <input
        type="text"
        placeholder="Search by patient or medication"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="prescription-search"
      />

      <ul className="prescription-list">
        {filtered.map((p) => (
          <li key={p.id}>
            <strong>{p.patient}</strong>: {p.medication} ({p.dosage})
          </li>
        ))}
        {prescriptions.map((p) => (
          <li key={p.id}>
            <strong>{p.patient}</strong>: {p.medication} ({p.dosage})
          </li>
        ))}
      </ul>

      {userRole === "doctor" && (
        <form className="prescription-form" onSubmit={handleSubmit}>
          <h3>Add Prescription</h3>
          <input
            type="text"
            name="patient"
            placeholder="Patient Name"
            value={form.patient}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="medication"
            placeholder="Medication"
            value={form.medication}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="dosage"
            placeholder="Dosage"
            value={form.dosage}
            onChange={handleChange}
            required
          />
          <button type="submit">Add Prescription</button>
        </form>
      )}
    </div>
  );
}

export default Prescriptions;
