import React, { useState } from 'react';
import {
  FileText, Pill, TestTube, CheckCircle, Clock, User, Users,
  Heart, Activity, AlertCircle, Calendar, Phone, Mail,
  Thermometer, Droplet, Wind, Plus, X, Save,
  Printer, Send, Eye, ChevronRight
} from 'lucide-react';
import './DoctorPortal.css';

const mockPatients = [
  {
    id: 'STRA-001',
    name: 'John Doe',
    age: 45,
    gender: 'Male',
    urgency: 'RED',
    waitTime: 5,
    symptoms: ['Chest Pain', 'Shortness of Breath', 'Dizziness'],
    history: 'Hypertension, Type 2 Diabetes',
    bloodType: 'O+',
    phone: '+254 712 345 678',
    email: 'john.doe@email.com',
    admissionDate: '2026-01-17',
    vitals: {
      temperature: '38.5°C',
      heartRate: '95 bpm',
      bloodPressure: '145/95',
      spo2: '97%',
      respiratoryRate: '20/min'
    },
    allergies: ['Penicillin', 'Aspirin'],
    currentMedications: ['Metformin 500mg', 'Lisinopril 10mg']
  },
  {
    id: 'STRA-002',
    name: 'Jane Smith',
    age: 32,
    gender: 'Female',
    urgency: 'YELLOW',
    waitTime: 15,
    symptoms: ['Headache', 'Fever', 'Fatigue'],
    history: 'Asthma',
    bloodType: 'A+',
    phone: '+254 723 456 789',
    email: 'jane.smith@email.com',
    admissionDate: '2026-01-17',
    vitals: {
      temperature: '37.8°C',
      heartRate: '82 bpm',
      bloodPressure: '120/80',
      spo2: '98%',
      respiratoryRate: '16/min'
    },
    allergies: ['None'],
    currentMedications: ['Ventolin Inhaler']
  },
  {
    id: 'STRA-003',
    name: 'Michael Johnson',
    age: 28,
    gender: 'Male',
    urgency: 'GREEN',
    waitTime: 30,
    symptoms: ['Minor Cut', 'Bruising'],
    history: 'None',
    bloodType: 'B+',
    phone: '+254 734 567 890',
    email: 'michael.j@email.com',
    admissionDate: '2026-01-17',
    vitals: {
      temperature: '36.6°C',
      heartRate: '72 bpm',
      bloodPressure: '118/75',
      spo2: '99%',
      respiratoryRate: '14/min'
    },
    allergies: ['None'],
    currentMedications: []
  }
];

export function DoctorPortal() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedPatient, setSelectedPatient] = useState(mockPatients[0]);
  const [clinicalNotes, setClinicalNotes] = useState('');

  const getUrgencyConfig = (urgency) => {
    switch (urgency) {
      case 'RED': 
        return { bg: 'bg-red-50', border: 'border-red-500', text: 'text-red-700', badge: 'bg-red-500' };
      case 'YELLOW': 
        return { bg: 'bg-yellow-50', border: 'border-yellow-500', text: 'text-yellow-700', badge: 'bg-yellow-500' };
      case 'GREEN': 
        return { bg: 'bg-green-50', border: 'border-green-500', text: 'text-green-700', badge: 'bg-green-500' };
      default: 
        return { bg: 'bg-gray-50', border: 'border-gray-500', text: 'text-gray-700', badge: 'bg-gray-500' };
    }
  };

  return (
    <div className="doctor-dashboard-grid">
      {/* Sidebar Navigation */}
      <aside className="doctor-dashboard-sidebar">
        {/* Sidebar content will be implemented next */}
        <div className="sidebar-placeholder">Sidebar</div>
      </aside>

      {/* Main Dashboard Content */}
      <main className="doctor-dashboard-main">
        {/* Header, stats cards, patient list, consultation, booking will be implemented next */}
        <div className="main-placeholder">Main Dashboard</div>
      </main>

      {/* Right Panel */}
      <aside className="doctor-dashboard-right-panel">
        {/* Doctor profile, stats, appointments, satisfaction chart will be implemented next */}
        <div className="right-panel-placeholder">Right Panel</div>
      </aside>
    </div>
  );
}
